CREATE OR REPLACE
    VIEW `spm_assignment`.`detailed_grades_entry` AS
select `spm_assignment`.`grades_entry`.`eid` AS `eid`,(case when (`spm_assignment`.`student`.`student_id` is not null) then `spm_assignment`.`student`.`student_id` else `spm_assignment`.`grades_entry`.`student_id` end) AS `student_id`,(case when (`spm_assignment`.`student`.`id_no` is not null) then `spm_assignment`.`student`.`id_no` else `spm_assignment`.`grades_entry`.`id_no` end) AS `id_no`,`spm_assignment`.`student`.`uid` AS `uid`,`spm_assignment`.`student`.`name` AS `name`,`spm_assignment`.`student`.`gender` AS `gender`,`spm_assignment`.`student`.`ethnic_group` AS `ethnic_group`,`spm_assignment`.`student`.`admission_year` AS `admission_year`,`spm_assignment`.`student`.`institute` AS `institute`,`spm_assignment`.`student`.`profession` AS `profession`,`spm_assignment`.`student`.`class_no` AS `class_no`,`spm_assignment`.`student`.`phone_no` AS `phone_no`,`spm_assignment`.`grades_entry`.`gid` AS `gid`,`spm_assignment`.`grades_entry`.`grades` AS `grades`,`spm_assignment`.`grades_entry`.`max_grades` AS `max_grades`,`spm_assignment`.`grades_entry`.`is_approved` AS `is_approved`,`spm_assignment`.`grades_entry`.`grades_problem1` AS `grades_problem1`,`spm_assignment`.`grades_entry`.`grades_problem2` AS `grades_problem2`,`spm_assignment`.`grades_entry`.`grades_problem3` AS `grades_problem3`,`spm_assignment`.`grades_entry`.`grades_problem4` AS `grades_problem4`,`spm_assignment`.`grades_entry`.`grades_problem5` AS `grades_problem5`,`spm_assignment`.`ccf_event`.`exam_no` AS `exam_no`,`spm_assignment`.`ccf_event`.`exam_time` AS `exam_time` from ((`spm_assignment`.`grades_entry` left join `spm_assignment`.`student` on(((`spm_assignment`.`grades_entry`.`student_id` = `spm_assignment`.`student`.`student_id`) or (`spm_assignment`.`grades_entry`.`id_no` = `spm_assignment`.`student`.`id_no`)))) join `spm_assignment`.`ccf_event` on((`spm_assignment`.`grades_entry`.`eid` = `spm_assignment`.`ccf_event`.`eid`)));
